#include <bits/stdc++.h>

using namespace std;

string ltrim(const string &);
string rtrim(const string &);

/*
 * Complete the 'count_word' function below.
 * 
 * The function is expected to return an INTEGER ARRAY.
 * The function accepts STRING ARRAY ss as parameter.
 * 
 */

vector<int> count_word(vector<string> ss) {
    vector<int> result;

    for (const string& s : ss) {
        result.push_back(s.length());
    }

    return result;
}

int main() {
    string ss_count_temp;
    getline(cin, ss_count_temp);

    int ss_count = stoi(ltrim(rtrim(ss_count_temp)));

    vector<string> ss(ss_count);

    for (int i = 0; i < ss_count; i++) {
        string ss_item;
        getline(cin, ss_item);
        ss[i] = ss_item;
    }

    vector<int> result = count_word(ss);

    for (int i = 0; i < result.size(); i++) {
        cout << result[i];

        if (i != result.size() - 1) {
            cout << "\n";
        }
    }

    return 0;
}

string ltrim(const string &str) {
    string s(str);

    s.erase(
        s.begin(),
        find_if(s.begin(), s.end(), not1(ptr_fun<int, int>(isspace)))
    );

    return s;
}

string rtrim(const string &str) {
    string s(str);

    s.erase(
        find_if(s.rbegin(), s.rend(), not1(ptr_fun<int, int>(isspace))).base(),
        s.end()
    );

    return s;
}

