def print_numbers_in_range(start, end):
    # Check for the range constraints
    if start < 1 or start > 100:
        print("Out of Range")
    elif end < 1 or end > 100:
        print("Out of Range")
    elif end - start != 9:
        print("Difference Not in Range")
    else:
        # If all conditions are met, print the 10 numbers
        numbers = list(range(start, end + 1))
        print(numbers)

# Input: number of test cases
T = int(input())

# Process each test case
for _ in range(T):
    # Input: start and end points for each test case
    start, end = map(int, input().split())
    print_numbers_in_range(start, end)

